import React, {Component} from 'react';
import axios from 'axios';
import {quizzes, users} from '../examples';

import {HTTP_SERVER_PORT} from '../constants.js';
import {HTTP_SERVER_PORT_PICTURES} from '../constants.js';
import {Link} from 'react-router-dom';

class List extends Component {

  constructor(props) {
    super(props)
    this.state = {
      quizes: null
    }
  }

  componentDidMount() {
    this.loadData()
  }

  async loadData() {
    console.log("chargement")
    const quizes = (await axios.get(HTTP_SERVER_PORT + 'quizes')).data;  // We need to wait for the response.
    this.setState({
      quizes: quizes
    });
    console.log(quizes)
  }          

    render() {
    return (
      <div id="container">
        <h1>List of Quiz</h1>
        <p>Choose a quiz to start playing</p>
        { this.state.quizes ?

          <div className="quiz-wrapper">
            {this.state.quizes.map((object, index) =>
              <Link to={{pathname: "/quizzes/"+ object._id, state: {
                quizz: {
                  _uid: 1,
                  name: 'quizz1',
                  icon: 'quizz1/quizz1.jpg',
                  keywords: ['kitchen', 'english'],
                  questions: [{
                    question: 'where is brian?',
                    video: null,
                    txtAnswers: ["in the living room", "in the kitchen", "in the garden", "in the bathroom"],
                    imgAnswers: [],
                    solutions: [1, 2],
                    points: 3
                  }, {
                    question: 'Where is Jenny, the sister of Brian?',
                    video: null,
                    txtAnswers: [],
                    imgAnswers: ["quizz1/bathroom.jpg", "quizz1/kitchen.jpg"],
                    solutions: [0],
                    points: 3
                  }, {
                    question: 'who is brian?',
                    video: null,
                    txtAnswers: ["a girl", "a boy"],
                    imgAnswers: [],
                    solutions: [0],
                    points: 1
                  }],
                  published: true,
                  ownerId: 1,
                  scores: []
                }
                }}} >
                <h3>{object.name}</h3>
                <img src={HTTP_SERVER_PORT_PICTURES + object.icon} />
              </Link>
            )}
          </div>

        : "Loading"
        }
      </div>
      
    );
  }
}

export default List;
